class NewsResponse {
  NewsResponse({
      this.status,
    this.code,
    this.message,
      this.totalResults, 
      this.articles,});

  NewsResponse.fromJson(dynamic json) {
    status = json['status'];

    code = json['code'];
    message = json['message'];

    totalResults = json['totalResults'];
    if (json['articles'] != null) {
      articles = [];
      json['articles'].forEach((v) {
        articles?.add(Articles.fromJson(v));
      });
    }
  }
  String? status;
  //we have to add code and massage manually becuse
  //if there is no source code and message will help us to know the problem
  String? code;
  String? message;

  num? totalResults;
  List<Articles>? articles;

  Map<String, dynamic> toJson() {
    final map = <String, dynamic>{};
    map['status'] = status;
    map['totalResults'] = totalResults;
    if (articles != null) {
      map['articles'] = articles?.map((v) => v.toJson()).toList();
    }
    return map;
  }

}

